import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Menu, X, ChevronDown, Phone, Mail, MapPin } from 'lucide-react';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isServicesOpen, setIsServicesOpen] = useState(false);
  const location = useLocation();

  const navItems = [
    { name: 'Home', path: '/' },
    { name: 'About Us', path: '/about' },
    { 
      name: 'Services', 
      path: '/services',
      hasDropdown: true,
      dropdownItems: [
        { name: 'Mobile Services', path: '/services/mobile' },
        { name: 'Internet Plans', path: '/services/internet' },
        { name: 'Business Solutions', path: '/services/business' },
        { name: 'Device Management', path: '/services/devices' },
        { name: 'Cloud Services', path: '/services/cloud' },
        { name: 'Network Infrastructure', path: '/services/network' },
      ]
    },
    { name: 'Careers', path: '/careers' },
    { name: 'Blog', path: '/blog' },
    { name: 'Contact', path: '/contact' },
  ];

  const isActivePath = (path) => {
    if (path === '/') {
      return location.pathname === '/';
    }
    return location.pathname.startsWith(path);
  };

  return (
    <>
      {/* Top Bar */}
      <div className="py-2 text-sm text-white bg-blue-900">
        <div className="container px-4 mx-auto">
          <div className="flex flex-col items-center justify-between space-y-2 lg:flex-row lg:space-y-0">
            <div className="flex flex-wrap items-center justify-center space-x-6 lg:justify-start">
              <div className="flex items-center space-x-2">
                <Phone size={14} />
                <span>+1 (555) 123-4567</span>
              </div>
              <div className="flex items-center space-x-2">
                <Mail size={14} />
                <span>info@zureetelecom.com</span>
              </div>
              <div className="flex items-center space-x-2">
                <MapPin size={14} />
                <span>New York, NY</span>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <span className="hidden md:inline">🕐 24/7 Customer Support</span>
              <Link 
                to="/login" 
                className="px-4 py-1 text-sm transition-colors bg-blue-700 rounded hover:bg-blue-600"
              >
                Login
              </Link>
            </div>
          </div>
        </div>
      </div>

      {/* Main Header */}
      <header className="sticky top-0 z-50 bg-white shadow-lg">
        <div className="container px-4 mx-auto">
          <div className="flex items-center justify-between py-4">
            {/* Logo */}
            <Link to="/" className="flex items-center space-x-3">
              <div className="p-3 text-white shadow-lg bg-gradient-to-br from-blue-600 to-blue-700 rounded-xl">
                <span className="text-2xl font-bold">Z</span>
              </div>
              <div>
                <h1 className="text-2xl font-bold tracking-tight text-gray-800">
                  Zuree Telecom
                </h1>
                <p className="text-sm font-medium text-blue-600">Connected Solutions</p>
              </div>
            </Link>

            {/* Desktop Navigation */}
            <nav className="items-center hidden space-x-8 lg:flex">
              {navItems.map((item, index) => (
                <div key={index} className="relative">
                  {item.hasDropdown ? (
                    <div className="relative group">
                      <button
                        className={`flex items-center space-x-1 py-2 px-3 rounded-lg text-gray-700 hover:text-blue-600 hover:bg-blue-50 transition-all font-medium ${
                          isActivePath(item.path) ? 'text-blue-600 bg-blue-50' : ''
                        }`}
                        onMouseEnter={() => setIsServicesOpen(true)}
                        onMouseLeave={() => setIsServicesOpen(false)}
                      >
                        <span>{item.name}</span>
                        <ChevronDown size={16} className="transition-transform group-hover:rotate-180" />
                      </button>
                      
                      {/* Dropdown */}
                      <div
                        className={`absolute top-full left-0 mt-2 w-64 bg-white rounded-xl shadow-xl border border-gray-100 py-4 opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-200 z-50`}
                        onMouseEnter={() => setIsServicesOpen(true)}
                        onMouseLeave={() => setIsServicesOpen(false)}
                      >
                        {item.dropdownItems.map((dropItem, dropIndex) => (
                          <Link
                            key={dropIndex}
                            to={dropItem.path}
                            className="block px-6 py-3 text-gray-700 transition-all hover:text-blue-600 hover:bg-blue-50"
                          >
                            {dropItem.name}
                          </Link>
                        ))}
                      </div>
                    </div>
                  ) : (
                    <Link
                      to={item.path}
                      className={`py-2 px-3 rounded-lg text-gray-700 hover:text-blue-600 hover:bg-blue-50 transition-all font-medium ${
                        isActivePath(item.path) ? 'text-blue-600 bg-blue-50' : ''
                      }`}
                    >
                      {item.name}
                    </Link>
                  )}
                </div>
              ))}
            </nav>

            {/* CTA Button & Mobile Menu */}
            <div className="flex items-center space-x-4">
              <Link
                to="/contact"
                className="hidden px-6 py-3 font-semibold text-white transition-all bg-blue-600 rounded-lg shadow-lg lg:inline-flex hover:bg-blue-700 hover:shadow-xl"
              >
                Get Started
              </Link>
              
              {/* Mobile menu button */}
              <button
                className="p-2 text-gray-600 transition-colors lg:hidden hover:text-blue-600"
                onClick={() => setIsMenuOpen(!isMenuOpen)}
              >
                {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
              </button>
            </div>
          </div>

          {/* Mobile Navigation */}
          {isMenuOpen && (
            <div className="py-4 border-t border-gray-100 lg:hidden">
              <nav className="flex flex-col space-y-2">
                {navItems.map((item, index) => (
                  <div key={index}>
                    {item.hasDropdown ? (
                      <div>
                        <button
                          className="flex items-center justify-between w-full px-4 py-3 text-gray-700 transition-all rounded-lg hover:text-blue-600 hover:bg-blue-50"
                          onClick={() => setIsServicesOpen(!isServicesOpen)}
                        >
                          <span className="font-medium">{item.name}</span>
                          <ChevronDown size={16} className={`transition-transform ${isServicesOpen ? 'rotate-180' : ''}`} />
                        </button>
                        {isServicesOpen && (
                          <div className="mt-2 ml-4 space-y-1">
                            {item.dropdownItems.map((dropItem, dropIndex) => (
                              <Link
                                key={dropIndex}
                                to={dropItem.path}
                                className="block px-4 py-2 text-gray-600 transition-all rounded-lg hover:text-blue-600 hover:bg-blue-50"
                                onClick={() => setIsMenuOpen(false)}
                              >
                                {dropItem.name}
                              </Link>
                            ))}
                          </div>
                        )}
                      </div>
                    ) : (
                      <Link
                        to={item.path}
                        className="block px-4 py-3 font-medium text-gray-700 transition-all rounded-lg hover:text-blue-600 hover:bg-blue-50"
                        onClick={() => setIsMenuOpen(false)}
                      >
                        {item.name}
                      </Link>
                    )}
                  </div>
                ))}
                <Link
                  to="/contact"
                  className="px-4 py-3 mt-4 font-semibold text-center text-white transition-all bg-blue-600 rounded-lg hover:bg-blue-700"
                  onClick={() => setIsMenuOpen(false)}
                >
                  Get Started
                </Link>
              </nav>
            </div>
          )}
        </div>
      </header>
    </>
  );
};

export default Header;
