import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// Import styles
import './styles/globals.css';

// Holographic Logo Component
const HolographicLogo = () => (
  <div className="relative w-16 h-16">
    <div className="absolute inset-0 holographic-border rounded-2xl">
      <div className="flex items-center justify-center w-full h-full glass-card rounded-2xl element-3d">
        <svg width="32" height="32" viewBox="0 0 24 24" className="holographic-text">
          <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5" fill="currentColor"/>
        </svg>
      </div>
    </div>
  </div>
);

// Animated Data Chart Component
const AnimatedDataChart = ({ title, data, color = "#00ffff" }) => {
  const [animatedData, setAnimatedData] = useState([]);
  
  useEffect(() => {
    const timer = setTimeout(() => {
      setAnimatedData(data);
    }, 500);
    return () => clearTimeout(timer);
  }, [data]);

  return (
    <div className="p-6 glass-neon rounded-3xl card-3d">
      <h4 className="mb-4 text-lg font-bold neon-text" style={{color}}>{title}</h4>
      <div className="space-y-3">
        {animatedData.map((item, index) => (
          <div key={index} className="relative">
            <div className="flex items-center justify-between mb-1">
              <span className="text-sm text-gray-300">{item.label}</span>
              <span className="text-sm font-bold" style={{color}}>{item.value}%</span>
            </div>
            <div className="relative h-3 overflow-hidden rounded-full bg-gray-800/50">
              <div 
                className="h-full transition-all duration-1000 ease-out rounded-full data-bar"
                style={{
                  width: `${item.value}%`,
                  background: `linear-gradient(90deg, ${color}40, ${color})`,
                  boxShadow: `0 0 10px ${color}80`,
                  animationDelay: `${index * 200}ms`
                }}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// 3D Holographic Card Component
const HolographicCard = ({ icon, title, description, index, gradient }) => (
  <div 
    className="relative group"
    style={{animationDelay: `${index * 200}ms`}}
  >
    <div className="holographic-border rounded-3xl">
      <div className="p-8 transition-all duration-500 glass-card rounded-3xl card-3d hover:scale-105">
        <div className="relative z-10">
          <div className="relative mb-6">
            <div className="text-6xl floating-element" style={{animationDelay: `${index * 300}ms`}}>
              {icon}
            </div>
          </div>
          
          <h3 className="mb-4 text-2xl font-bold holographic-text">
            {title}
          </h3>
          <p className="mb-6 leading-relaxed text-gray-300">{description}</p>
          
          <div className="flex items-center justify-between">
            <button className="text-sm neon-button">
              EXPLORE
            </button>
            <div className="w-2 h-2 rounded-full pulse-neon bg-cyan-400"></div>
          </div>
        </div>
      </div>
    </div>
  </div>
);

// Interactive Infographic Component
const InteractiveInfographic = ({ stats }) => (
  <div className="grid grid-cols-2 gap-6 lg:grid-cols-4">
    {stats.map((stat, index) => (
      <div key={index} className="infographic-item">
        <div className="p-6 text-center glass-neon rounded-2xl element-3d">
          <div className="relative mx-auto mb-4 circular-chart">
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="z-10 text-center">
                <div className="text-2xl font-bold holographic-text">
                  {stat.value}
                </div>
              </div>
            </div>
          </div>
          <div className="mb-1 text-sm text-gray-300">{stat.label}</div>
          <div className="text-xs text-cyan-400 neon-text">{stat.description}</div>
        </div>
      </div>
    ))}
  </div>
);

// Enhanced Header Component
const FuturisticHeader = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const navItems = [
    'Home', 'About Us', 'Our Services', 'Communities', 'Our Blogs', 'Contact Us', 'Login'
  ];

  return (
    <header className={`fixed w-full top-0 z-50 transition-all duration-700 ${
      isScrolled 
        ? 'glass-card backdrop-blur-3xl' 
        : 'bg-transparent'
    }`}>
      <div className="container px-4 mx-auto">
        <div className="flex items-center justify-between py-4">
          {/* Logo */}
          <div className="flex items-center space-x-4">
            <HolographicLogo />
            <div>
              <h1 className="text-2xl font-bold holographic-text">
                Zuree Telecom
              </h1>
              <p className="text-sm text-cyan-400 neon-text">Connected Solutions</p>
            </div>
          </div>

          {/* Desktop Navigation */}
          <nav className="items-center hidden space-x-2 lg:flex">
            {navItems.map((item, index) => (
              <div key={index} className="relative">
                <button className="relative px-6 py-3 font-medium transition-all duration-300 rounded-xl text-white/80 hover:text-white group">
                  <span className="relative z-10">{item}</span>
                  <div className="absolute inset-0 transition-opacity duration-300 opacity-0 glass-neon group-hover:opacity-100 rounded-xl"></div>
                </button>
              </div>
            ))}
          </nav>

          {/* Mobile Menu Button */}
          <button 
